rs = sg.queryPgql("SELECT a MATCH (a) WHERE a.account_no = 'xxx-yyy-201'")
for (r in rs) {
  v = r.getVertex(1)
  partition = result.getPartitionByVertex(v);
  partition.each{println it.getProperty("account_no")}
}

