%pgx
// Find all the vertices with the label "Person" and return their names and dates of birth (dob)
graph.queryPgql("""
SELECT n.name, n.dob
  FROM MATCH (n:Person)
""");

%pgx
// Find all the edges with the label "knows" and return the source persons and destination persons
graph.queryPgql("""
SELECT a.name AS a, b.name AS b
  FROM MATCH (a:Person) -[e:knows]-> (b:Person)
""");

%pgx
// Find all the vertices with the label "Person" or "University"
graph.queryPgql("""
SELECT n.name, n.dob
  FROM MATCH (n:Person|University)
""");

%pgx
// Find all the vertices with any or no label
graph.queryPgql("""
SELECT n.name, n.dob
  FROM MATCH (n)
""");
